import web,db,settingsurl = ('^/product/add[/]?$','product_add',)render = web.template.render(settings.TEMPLATE_FOLDER)class product_add(object):	def GET(self):		return render.product.add()			def POST(self):		data = web.input()		db.db.insert("t_product",name=data.name,description=data.desc)		return web.seeother('/product/add')		if __name__ == "__main__":	app = web.application(url,globals())	app.run()		